import React, { useState } from 'react';
import CommonFields from './CommonFields';

export default function Form1() {
    const [formData, setFormData] = useState({
      n: null,
    });
  
    const handleChange = (e) => {
      const { name, value } = e.target;
      setFormData({ ...formData, [name]: value });
    };
  
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
      };

      const [result, setResult] = useState(null);
  
    const handleSubmit = (e) => {
      e.preventDefault();
      // Calculate the area here using the provided formula
      console.log(formData)
      const { ab, bc, cd, da, bd } = formData;
      const area = ab + bc + cd + /* Perform your area calculation here */
      setResult(area);
    };
  
    return (
      <>
      <form method="post" novalidate="on" onSubmit={handleSubmit}>
          <h4>This Form is for Land Division in Equal Part</h4>
          <p><i>Each length must be in the same unit</i></p>

          < CommonFields />

          <p>
            <label htmlFor="id_n">n:</label>
            <input
            type="number"
            name="n"
            step="any"
            required
            id="id_bd"
            value={formData.n}
            onChange={handleInputChange}
            />
        </p>
  
          <button type="submit" name="form_type" value="form1" className="btnstyle">
            Get Result
          </button>
        </form>
        <p className="pstyle"><i><b>Note: </b></i>we have to make equal section on front side of field</p>
        <p className="pstyle">AP<sub>1</sub>=P<sub>1</sub>P<sub>2</sub>=P<sub>2</sub>P<sub>3</sub>=...=P<sub>n-1</sub>B</p>
        {result !== null && (
          <div>
            <h2>Result</h2>
            <p>The calculated area is: {result} square units</p>
          </div>
        )}
      </>
    );
  }
